import React, { Component } from "react";
import { Outlet } from "react-router-dom";

//  Keeping the below in to remind of Link import in case there are issues in future
//  import { Outlet, Link } from "react-router-dom";

// Styling
import "./css-sheets/App.css";

// COMPONENTS
import Header from "./components/Header.js";
import HousePlants from "./page-routes/HousePlants.js";

//ADDING baseURL
let baseURL = "";

if (process.env.NODE_ENV === "development") {
  baseURL = "http://localhost:3000";
} else {
  baseURL = "heroku backend url";
}
console.log("current base url:", baseURL);

// CLASS BASED COMPONENT
class App extends Component {

  // This is a method because it is associated with a class
  getPlants = () => {
    fetch(baseURL + "/plants")
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          return [];
        }
      })
      .then(data => {
        console.log("data:", data);
        this.setState({ plants: data.plants });
      });
  };

  componentDidMount
  componentDidMount() {
    this.getPlants();
  }

  // const [data, setData] = useState({
  //   name: ""
  // })




  render() {   
    return (
      <div className="App">
         <div className="Title">

    <h1>POTTED PALS</h1>
    <nav className="Navbar">
      <Link to="/succulents" state={{ data: data }}>SUCCULENTS</Link> 
      <Link to="./flowers" state={{ data: data }}>FLOWERS</Link>
      <Link to="./houseplants" state={{ data: data }}>HOUSE PLANTS</Link>
      <Link to="./herbs" state={{ data: data }}>HERBS</Link>         
    </nav>
  </div>

        <Outlet />
      </div>
    );
  }
}

export default App;
