import React, { Component } from "react";

//COMPONENTS
import PlantCard from "../components/PlantCard.js";

// CLASS BASED COMPONENT
class Herbs extends Component {
  render() {
    return (
      <main>
        <h2 style={{ margintop: "1px" }}>HERBS</h2>
        {this.state.plants.map((plant)=>
         <PlantCard key={plant._id} img={plant.img} name ={plant.name} type={plant.type} description={plant.description}></PlantCard>
)}

        <Outlet />

        <PlantCard name="Herb" description="Adding a herb to the herb page" />
      </main>
    );
  }
}

export default Herbs;
